mapper.select:
select * from data_dictionary


    and typeCode = #{typeCode}


    and typeName = #{typeName}


    and id = #{id}

  order by valueId asc

mapper.delete:
delete from data_dictionary


    and id = #{id}


    and typeCode = #{typeCode}

mapper.update:
update data_dictionary

   typeCode = #{typeCode},
   typeName = #{typeName},
   valueId = #{valueId},
   valueName = #{valueName}

where id = #{id}

mapper.select:
select count(1) from data_dictionary


    and typeCode = #{typeCode}


    and valueId = #{valueId}


    and id != #{id}
